from flask import Flask, request, jsonify
from dataclasses import dataclass
from typing import List, Optional

app = Flask(__name__)

@dataclass
class MindMapNode:
    id: str
    title: str
    status: str
    priority: str
    children: Optional[List['MindMapNode']] = None

@app.route('/api/nodes', methods=['GET', 'POST'])
def handle_nodes():
    if request.method == 'GET':
        return jsonify(get_all_nodes())
    elif request.method == 'POST':
        node_data = request.get_json()
        return jsonify(create_node(node_data))

if __name__ == '__main__':
    app.run(debug=True)

// Monitoring and analytics setup
const analytics = {
  trackEvent: (event, properties) => {
    console.log('Analytics Event:', event, properties);
    // Send to analytics service
    fetch('/api/analytics', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ event, properties, timestamp: Date.now() })
    });
  },

  trackNodeInteraction: (action, nodeId) => {
    analytics.trackEvent('node_interaction', {
      action,
      nodeId,
      sessionId: getSessionId()
    });
  },

  trackPerformance: (metric, value) => {
    analytics.trackEvent('performance', {
      metric,
      value,
      userAgent: navigator.userAgent
    });
  }
};

// Jest unit tests for mind map functionality
describe('MindMapRenderer', () => {
  let renderer;

  beforeEach(() => {
    renderer = new MindMapRenderer();
  });

  test('should render nodes correctly', () => {
    const testData = {
      nodes: [{ id: '1', title: 'Test Node', status: 'pending' }]
    };

    renderer.loadData(testData);
    const container = document.createElement('div');
    renderer.render(container);

    expect(container.querySelectorAll('.node')).toHaveLength(1);
    expect(container.querySelector('.node-title').textContent).toBe('Test Node');
  });

  test('should update progress correctly', () => {
    const nodes = [
      { id: '1', status: 'completed' },
      { id: '2', status: 'pending' }
    ];

    const progress = renderer.calculateProgress(nodes);
    expect(progress.percentage).toBe(50);
  });
});

// Playwright E2E tests
const { test, expect } = require('@playwright/test');

test.describe('Mind Map Application', () => {
  test('should create and manage nodes', async ({ page }) => {
    await page.goto('http://localhost:3000');

    // Create a new node
    await page.click('[data-testid="add-node"]');
    await page.fill('[data-testid="node-title-input"]', 'New Test Node');
    await page.press('[data-testid="node-title-input"]', 'Enter');

    // Verify node was created
    await expect(page.locator('.node')).toContainText('New Test Node');

    // Change status
    await page.click('.status-icon');
    await expect(page.locator('.node')).toHaveClass(/status-in-progress/);

    // Add child node
    await page.click('.icon-add');
    await expect(page.locator('.node-parent .node')).toHaveCount(1);
  });

  test('should save and load data correctly', async ({ page }) => {
    await page.goto('http://localhost:3000');
    await page.click('#saveBtn');
    await expect(page.locator('#saveIndicator')).toContainText('Saved');
  });
});
